INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3822500', 'deDE', 'Tod der Hexenmutter!', 'Tötet Magula.', 'Die unzähligen Jahre meines Lebens habe ich dem Erlernen und Perfektionieren der Heil- und Regenerationskünste gewidmet.$b$bNichts in der Welt quält mich mehr, als Leben zu nehmen, $gReisender:Reisende;.$b$bUnd darum verurteile ich nur schweren Herzens Magula, die Anführerin der Harpyien, die diesen Hain bedrohen, zum Tode.$b$bMöge ihr Tod das Leben edlerer Lebewesen retten.', '', '', 'Magula', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3822500', 'itIT', 'Morte alla Strega Madre', 'Uccidi Magula.', 'Gli innumerevoli anni della mia vita sono stati dedicati all\'apprendimento e al perfezionamento delle arti di cura e rigenerazione.$b$bNiente in questo mondo mi arreca più dolore che uccidere qualcuno, viandante.$b$bÈ con grande rammarico che devo chiederti di uccidere Magula, la creatura a capo delle Arpie che minacciano questo boschetto.$b$bPossa la sua morte salvare quella di altri nobili esseri.', '', '', 'Magula', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3822500', 'esES', 'Matar a la bruja madre', 'Mata a Magula.', 'He dedicado innumerables años de mi vida a aprender y perfeccionar las artes de la sanación y la restauración.$B$BNada en este mundo me duele más que arrebatar una vida, $gviajero:viajera;.$B$BMuy a mi pesar, debo ordenar la muerte de Magula, la líder de las arpías que amenazan esta arboleda.$B$BOjalá su muerte salve la vida a seres más nobles.', '', '', 'Magula', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3822500', 'ptBR', 'Morte à Bruxa Matriarca', 'Mate Magula.', 'Os incontáveis anos da minha vida foram dedicados ao aperfeiçoamento da arte da cura e da restauração.$b$bNada nesse mundo me dói mais do que tirar uma vida, viajante.$b$bÉ com o coração pesado que exijo a morte de Magula, a líder das harpias que ameaçam este bosque.$b$bQue sua morte sirva para salvar a vida de criaturas mais nobres.', '', '', 'Magula', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3822500', 'koKR', '마녀어미에게 죽음을', '마굴라를 처치해야 합니다.', '나는 아득히 오래전부터 치유와 회복 기술을 배우고 익히는 데 전념했지.$b$b누군가를 해친다는 생각만으로도 끔찍하게 고통스럽다.$b$b그런 내가 이런 부탁을 하자니 마음이 참 무겁군. 이 숲을 위협하는 하피 떼의 우두머리인 마굴라를 처치해다오.$b$b마굴라의 죽음이 더 귀중한 생명을 살릴 수 있기를.', '', '', '마굴라', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3822500', 'zhCN', '巫母之死', '消灭玛古拉。', '我这漫长的一生都致力于学习并完善治疗与恢复的艺术。$b$b世上让我最痛心的事莫过于杀生，旅行者。$b$b我怀着无比沉痛的心情委托你去消灭鹰身人的首领玛古拉，她们已经威胁到了这片林地。$b$b但愿她的死亡能够挽救更多高贵的生命。', '', '', '玛古拉', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3822500', 'zhTW', '巫母受死', '殺死瑪古菈。', '我將數不盡的歲月投注於學習和精進治療與恢復的技藝。$b$b這世上最讓我痛苦的事莫過於殺生，旅行者。$b$b我必須懷著無比沉痛的心情委託你殺死瑪古菈，殺死威脅這片林地的鷹身人首領。$b$b願她的死亡能夠挽救更多高貴的生命。', '', '', '瑪古菈', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3822500', 'ruRU', 'Смерть ведьме-матери!', 'Убейте Магулу.', 'Я давно потерял счет годам, прожитым мною на этой земле, – и все это время было посвящено изучению и совершествованию искусства исцеления.$b$bНичто в этом мире не ранит меня больнее, чем необходимость отнять чужую жизнь.$b$bС болью в сердце я вынужден просить тебя убить Магулу, предводительницу гарпий, которые угрожают нашей роще.$b$bПусть ее смерть станет залогом процветания более благородных созданий природы.', '', '', 'Магула', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3822500', 'esMX', 'Muerte a la Bruja Madre', 'Asesina a Magula.', 'Dediqué innumerables años de mi vida a aprender y perfeccionar las artes de la sanación y la restauración.$b$bNada en este mundo me duele más que tomar una vida, $Gviajero:viajera;.$b$bEs con mucha tristeza que debo encomendarte la muerte de Magula, la líder de las arpías que amenazan esta arboleda.$b$bQue su muerte salve las vidas de muchos seres nobles.', '', '', 'Magula', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3822500', 'frFR', 'Mort à la mère sorcière', 'Tuez Magula.', 'J’ai passé le plus clair de ma vie à apprendre les arts de la guérison et de la restauration, et à me perfectionner dans leur utilisation.$b$bRien ne peut me peiner plus que de prendre une vie, $gvoyageur:voyageuse;.$b$bC’est donc le cœur gros que je vous demande de tuer Magula, qui dirige les harpies infestant ce bosquet.$b$bPuisse sa mort sauver la vie d’êtres plus nobles qu’elle !', '', '', 'Magula', '', '', '', '26365');
