INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938400', 'frFR', 'Le Corrupteur', 'Tuez Kagraxxis le Corrupteur.', 'Xandris est mort, mais nous avons d’autres ennemis à affronter si nous voulons sauver mon bosquet.$b$bPendant la grande guerre, Kagraxxis a pris un malin plaisir à corrompre les terres qui abritaient autrefois son foyer.$b$bSans la stupide clémence de mes compagnons druides, je l’aurais tué de mes propres mains.$b$bTrouvez-moi ce démon, et que justice soit faite !$b$bVous devrez accomplir cette tâche $gseul:seule;, car je sens ici une présence encore plus sombre… une présence dont je vais devoir m’occuper personnellement.', '', '', 'Kagraxxis le Corrupteur', '', '', '', '26365');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938400', 'deDE', 'Der Verderber', 'Tötet Kagraxxis den Verderber.', 'Xandris ist erledigt, aber wir müssen uns noch um andere kümmern, um meinen Hain zu retten.$b$bWährend des Krieges war es Kagraxxis ein großes Vergnügen, das Land zu verderben, das er einst seine Heimat nannte.$b$bWenn die anderen Druiden ihm nicht törichterweise Gnade gewährt hätten, hätte ich ihn selbst hingerichtet.$b$bFindet diesen Dämon und lasst ihm Gerechtigkeit widerfahren.$b$bIch muss Euch diese Aufgabe übertragen, denn ich verspüre die Anwesenheit einer noch dunkleren Präsenz... einer Präsenz, die nach meiner persönlichen Aufmerksamkeit verlangt.', '', '', 'Kagraxxis der Verderber', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938400', 'itIT', 'Il Corruttore', 'Uccidi Kagraxxis il Corruttore.', 'Xandris è stato ucciso, ma dobbiamo occuparci di altri demoni se vogliamo salvare il mio boschetto.$b$bDurante la Grande Guerra, Kagraxxis ha provato un immenso piacere nel corrompere le terre che un tempo chiamava casa.$b$bSe non fosse stato per i miei compagni Druidi, l\'avrei ucciso io stesso.$b$bTrova il demone e fai calare la giustizia su di lui.$b$bDevo affidare questo incarico a te, poiché sento una presenza ancora più oscura qui, che richiede la mia personale attenzione.', '', '', 'Kagraxxis il Corruttore', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938400', 'esES', 'El Corruptor', 'Mata a Kagraxxis el Corruptor.', 'Xandris ha muerto, pero hay otros de los que encargarse para que mi arboleda se salve.$B$BDurante la gran guerra, Kagraxxis disfrutó corrompiendo las tierras que en otro tiempo llamaba hogar.$B$BDe no haber sido por la estúpida compasión de mis colegas druidas, yo lo habría matado personalmente.$B$BEncuentra al demonio y ajustícialo.$B$BDebo confiarte esta tarea a ti $gsolo:sola;, pues siento una presencia aún más oscura aquí... Una que requiere mi atención personal.', '', '', 'Kagraxxis el Corruptor', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938400', 'ptBR', 'O Corruptor', 'Mate Kagraxxis, o Corruptor.', 'Xandris está morto, mas há outras criaturas pondo o bosque em risco.$b$bDurante a grande guerra, Kagraxxis sentia imenso prazer em corromper as terras que outrora o abrigaram.$b$bNão fosse pela misericórdia tola de meus colegas druidas, eu mesmo o teria matado.$b$bEncontre o demônio e cumpra a justiça.$b$bDeixarei a tarefa a seu encargo, pois sinto uma presença ainda mais sombria aqui... uma presença que requer minha atenção.', '', '', 'Kagraxxis, o Corruptor', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938400', 'koKR', '타락자', '타락자 카그락시스를 처치해야 합니다.', '잔드리스를 없앴지만, 이 숲을 살리려면 아직도 처치해야 할 자들이 남았다.$b$b대전쟁 중에 카그락시스는 한때 고향이라 불렀던 곳을 앞장서서 타락시켰지.$b$b내 형제 드루이드들이 어리석게 자비를 베풀지만 않았어도 내가 직접 그를 없앴을 것이다.$b$b그 악마를 찾아 정의의 심판을 내려 다오.$b$b이 임무는 그대 혼자 처리해야 할 것 같다. 지금 이곳에서 더욱 어두운 존재가 느껴진다... 나는 그쪽을 살펴야 한다.', '', '', '타락자 카그락시스', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938400', 'zhCN', '腐蚀者', '消灭腐蚀者康格拉克斯。', '虽然除掉了萨德里斯，但是我们仍需清除更多的威胁才能拯救我的林地。$b$b大战时，康格拉克斯在对自己家乡的腐化中获得了极大的快感。$b$b要不是因为德鲁伊同胞们泛滥的怜悯心，我早就亲手杀了他。$b$b请你去找到这个恶魔并且把他就地正法。$b$b之所以把这个任务托付给你，是因为我在这里感受到了更加黑暗的力量……我必须谨慎处理。', '', '', '腐蚀者康格拉克斯', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938400', 'zhTW', '墮落者', '殺死『墮落者』卡格拉西斯。', '山德里斯已經死了，但還有其他敵人必須處理，才能確保這片林地的安全。$b$b在先祖之戰中，卡格拉西斯非常樂於腐化曾是他家園的這片土地。$b$b要不是其他德魯伊愚蠢地選擇仁慈，我早就把他殺了。$b$b找出惡魔，並伸張正義。$b$b我必須把這項工作全權交由你處理。我感受到此地傳來一股更黑暗的氣息…需要我親自動手。', '', '', '『墮落者』卡拉格西斯', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938400', 'ruRU', 'Осквернитель', 'Убейте Каграксиса Осквернителя.', 'С Ксандрисом покончено, но многие из его сподвижников еще живы, и мы должны разделаться и с ними, чтобы спасти мою рощу.$b$bВо время великой войны Каграксис с особенным наслаждением осквернял земли, когда-то бывшие для него родным домом.$b$bЕсли бы не эта глупая жалость, которую к нему проявили мои товарищи-друиды, я бы лично прикончил его.$b$bОтыщи демона и заставь его заплатить за содеянное.$b$bЭто тебе придется сделать уже без моей помощи, ибо я ощущаю здесь присутствие еще более могучей темной силы, и мне нужно выяснить, что это такое.', '', '', 'Каграксис Осквернитель', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938400', 'esMX', 'El Corruptor', 'Mata a Kagraxxis el Corruptor.', 'Xandris fue asesinado, pero quedan más demonios de los que debemos encargarnos si queremos salvar mi arboleda.$b$bDurante la gran guerra, Kagraxxis tomó gran placer en corromper las tierras que él mismo llamaba hogar.$b$bSi no fuera por la tonta misericordia de mis compañeros druidas, me habría encargado de él yo mismo.$b$bEsta tarea te la confío solo a ti, pues siento una presencia todavía más oscura... una que requiere mi atención personal.', '', '', 'Kagraxxis el Corruptor', '', '', '', '25549');
