INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3838200', 'frFR', 'L’archidruide du Val', 'Parlez à Thaon Griffelune.', 'Au cœur de notre forêt s’étend le val des Griffelunes.$b$bVous y trouverez un groupe de druides farouches reclus, chargés de la protection de Val’sharah contre tout ce qui pourrait perturber l’équilibre naturel.$b$bIls sont sous les ordres de Thaon Griffelune, le plus sauvage des archidruides.$b$bRetrouvez-le. Il faut qu’il sache que Cénarius a besoin de son aide.', '', '', '', '', '', 'Parlez à Thaon Griffelune dans le Val des Griffelunes.', '26365');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3838200', 'deDE', 'Erzdruide des Tals', 'Sprecht mit Thaon Mondklaue.', 'Tief in unserem Wald verborgen liegt das Mondklauental.$b$bDort findet Ihr eine zurückgezogene Gruppe wilder Druiden, die damit beauftragt sind, Val\'sharah vor allem zu beschützen, was den Einklang der Natur stören könnte.$b$bDer Anführer dieser Druiden ist Thaon Mondklaue, der entschlossenste Kämpfer der Erzdruiden.$b$bSucht ihn auf und teilt ihm mit, dass Cenarius seine Hilfe benötigt.', '', '', '', '', '', 'Sprecht mit Thaon Mondklaue im Mondklauental.', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3838200', 'itIT', 'L\'Arcidruido della Vallata', 'Parla con Thaon Zannaluna.', 'La Vallata degli Zannaluna si trova nelle profondità della nostra foresta.$b$bLà troverai un gruppo solitario di Druidi Ferini incaricati di proteggere Val\'sharah da chiunque voglia rompere l\'equilibrio della natura.$b$bQuesti Druidi sono guidati da Thaon Zannaluna, il più feroce degli Arcidruidi.$b$bCercalo e fagli sapere che Cenarius ha bisogno del suo aiuto.', '', '', '', '', '', 'Parla con Thaon Zannaluna alla Vallata degli Zannaluna.', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3838200', 'ptBR', 'Arquidruida do Vale', 'Fale com Thaon Garralua.', 'Embrenhado na floresta, há um vale chamado Garralua.$b$bLá você encontrará um grupo recluso de druidas ferais encarregados de proteger Val\'sharah de qualquer coisa que desfaça o equilíbrio da natureza.$b$bOs druidas são liderados por Thaon Garralua, o mais bravo dos arquidruidas.$b$bEncontre-o. Diga que Cenarius se encontra em maus lençóis.', '', '', '', '', '', 'Fale com Thaon Garralua no Vale Garralua.', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3838200', 'koKR', '골짜기의 대드루이드', '테온 문클로와 대화해야 합니다.', '숲속 깊이 달발톱 골짜기가 있습니다.$b$b골짜기에는 외부와의 접촉을 꺼리는 야성 드루이드 무리가 살면서, 누구도 자연의 균형을 깨뜨리지 못하도록 발샤라를 보호하고 있지요.$b$b이들을 이끄는 사람은 대드루이드 중에서도 용맹하기로 소문난 테온 문클로입니다.$b$b그를 찾아 세나리우스 님의 상황을 설명하고 도움이 필요하다고 전해 주세요.', '', '', '', '', '', '달발톱 골짜기에 있는 테온 문클로와 대화해야 합니다.', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3838200', 'zhCN', '谷地大德鲁伊', '与绍恩·月爪谈一谈。', '月爪谷位于森林深处。$b$b那里隐居着一群野性德鲁伊，他们保护着瓦尔莎拉的自然平衡。$b$b绍恩·月爪是这群德鲁伊的领袖，他是大德鲁伊中最勇猛的一位。$b$b去找他，告诉他塞纳留斯需要他的帮助。', '', '', '', '', '', '在月爪谷与绍恩·月爪谈一谈。', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3838200', 'zhTW', '山谷大德魯伊', '與薩翁‧月爪談話。', '月爪谷就位在這片森林深處。$b$b那裡住著一群隱居的野性德魯伊，負責守護維爾薩拉、防止任何人打破自然平衡。$b$b這群德魯伊的領導者是薩翁‧月爪，他是最兇猛的大德魯伊。$b$b請找到他，告訴他塞納留斯需要他的協助。', '', '', '', '', '', '在月爪谷與薩翁‧月爪交談。', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3838200', 'ruRU', 'Верховный друид долины', 'Поговорите с Таоном Лунным Когтем.', 'В глубине нашего леса лежит долина Лунного Когтя.$b$bТам ты найдешь группу друидов-оборотней — отшельников, ревностно защищающих Валь\'шару от всего, что угрожает природному равновесию.$b$bВозглавляет их Таон Лунный Коготь, самый свирепый из всех верховных друидов.$b$bОтыщи его. Скажи, что Кенарию срочно требуется его помощь.', '', '', '', '', '', 'Поговорите с Таоном Лунным Когтем в долине Лунного Когтя.', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3838200', 'esES', 'Archidruida del Valle', 'Habla con Thaon Zarpaluna.', 'En lo más hondo de nuestro bosque se encuentra el Valle Zarpaluna.$B$BAllí encontrarás a un grupo de druidas ferales ermitaños que tiene la misión de proteger Val\'sharah de cualquiera que pretenda alterar el equilibrio de la naturaleza.$B$BEstos druidas están liderados por Thaon Zarpaluna, el más feroz de los archidruidas.$B$BBúscalo. Dile que Cenarius necesita su ayuda.', '', '', '', '', '', 'Habla con: Thaon Zarpaluna. Zona: Valle Zarpaluna.', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3838200', 'esMX', 'Archidruida del valle', 'Habla con Thaon Zarpaluna.', 'En lo profundo de nuestro bosque yace el Valle Zarpaluna.$b$bAllí encontrarás a un grupo aislado de druidas ferales encargados de proteger a Val\'sharah de cualquiera que busque perturbar el equilibrio de la naturaleza.$b$bEstos druidas son liderados por Thaon Zarpaluna, el archidruida más feroz.$b$bBúscalo. Hazle saber que Cenarius necesita su ayuda.', '', '', '', '', '', 'Habla con Thaon Zarpaluna en el Valle Zarpaluna.', '25549');
