INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3814200', 'deDE', 'Erzdruidin der Klaue', 'Sucht das Stahlklauental.', 'Nahe des südlichen Waldrands findet Ihr die Höhle der Schläfer.$B$BAn diesem Ort kommen die Erzdruidin Koda Stahlklaue und ihre eifrigen Schüler ihrer heiligen Pflicht nach, den Smaragdgrünen Traum zu beobachten.$b$bFindet die Schülerin Rylissa Bärensang im Stahlklauental und informiert sie über die Krise im Hain des Cenarius.', '', '', '', '', '', 'Sprecht mit Rylissa Bärensang im Stahlklauental.', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3814200', 'itIT', 'L\'Arcidruida dell\'Unghia', 'Trova la Vallata Zannaferrea.', 'Troverai il Riparo del Dormiente all\'estremità settentrionale della foresta.$B$BIn quel luogo, l\'Arcidruida Koda Zannaferrea e i suoi devoti discepoli svolgono il loro sacro dovere osservando il Sogno di Smeraldo.$b$bTrova Rylissa Cantaorsi nella Vallata Zannaferrea e informala sulla situazione al Boschetto di Cenarius.', '', '', '', '', '', 'Parla con Rylissa Cantaorsi alla Vallata Zannaferrea.', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3814200', 'ptBR', 'Arquidruidesa da Garra', 'Encontre o Vale Garra de Aço.', 'Perto da borda sul da nossa floresta, você encontrará a Gruta do Adormecido.$B$BLá a arquidruidesa Koda Garra de Aço e seus dedicados discípulos cumprem o dever sagrado de observar o Sonho Esmeralda.$b$bEncontre a discípula Rylissa Ursocanto no Vale Garra de Aço e alerte-a da crise instaurada no Bosque de Cenarius.', '', '', '', '', '', 'Fale com Rylissa Ursocanto no Vale Garra de Aço.', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3814200', 'koKR', '발톱의 대드루이드', '스틸클로 골짜기를 찾아야 합니다.', '숲의 남쪽 끝 부근에 잠자는 자의 지하굴이 있습니다.$B$B그곳에서 대드루이드 코다 스틸클로와 그 제자들이 에메랄드의 꿈을 지켜보는 신성한 임무를 수행하고 있지요.$b$b스틸클로 골짜기에 있는 라일리사 베어송을 찾아가, 세나리우스의 숲에 위기가 닥쳤다고 전해 주세요.', '', '', '', '', '', '스틸클로 골짜기에 있는 라일리사 베어송과 대화해야 합니다.', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3814200', 'zhCN', '利爪大德鲁伊', '找到钢爪谷。', '你可以在我们森林的最南边找到沉眠者地穴。$B$B大德鲁伊科达·钢爪和她的忠实信徒就是在那里履行着监视翡翠梦境的神圣职责。$b$b到钢爪谷找瑞丽萨·熊歌，把塞纳留斯林地的危机告诉她。', '', '', '', '', '', '在钢爪谷与瑞丽萨·熊歌谈一谈。', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3814200', 'zhTW', '利爪大德魯伊', '找到鋼爪谷。', '你可以在靠近森林南方的邊緣地帶找到眠者獸穴。$B$B大德魯伊蔻妲‧鋼爪和她忠誠的侍徒就在那裡負起監視翡翠夢境的神聖職責。$b$b到鋼爪谷找萊麗莎‧熊歌，告訴她塞納留斯林地的危機。', '', '', '', '', '', '在鋼爪谷和萊麗莎‧熊歌交談。', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3814200', 'ruRU', 'Верховный друид-медведь', 'Отыщите долину Стального Когтя.', 'У южного края нашего леса ты найдешь Обитель Спящей. $B$BТам верховный друид Кода Стальной Коготь и верные ей ученики исполняют свой священный долг, приглядывая за Изумрудным Сном.$b$bОтыщи в долине Стального Когтя одну из учениц, Рилиссу Медвежья Песнь, и расскажи ей о том, что творится в Роще Кенария.', '', '', '', '', '', 'Поговорите с Рилиссой Медвежья Песнь в долине Стального Когтя.', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3814200', 'esES', 'Archidruida de la Zarpa', 'Localiza el Valle Zarpacerada.', 'Cerca del extremo sur de nuestro bosque encontrarás el Túmulo del Durmiente.$b$bAllí, la archidruida Koda Zarpacerada y sus entregados discípulos cumplen con el deber sagrado de proteger el Sueño Esmeralda.$b$bBusca a la discípula Rylissa Cantodeoso en el Valle Zarpacerada e infórmala de la crisis en la Arboleda de Cenarius.', '', '', '', '', '', 'Habla con: Rylissa Cantodeoso. Zona: Valle Zarpacerada.', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3814200', 'esMX', 'Archidruida de la Zarpa', 'Encuentra el Valle garra de acero.', 'Cerca del linde sur de nuestro bosque encontrarás el Túmulo del Durmiente.$B$BAllí, la archidruida Koda Garra de Acero y sus discípulos fieles cumplen con su deber sagrado de observar el Sueño Esmeralda.$b$bEncuentra a la discípula Rylissa Canción de Oso en el Valle Garra de Acero y avísale de la crisis en la Arboleda de Cenarius.', '', '', '', '', '', 'Habla con Rylissa Canción de Oso en el Valle garra de acero.', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3814200', 'frFR', 'L’archidruidesse de la Griffe', 'Trouvez le Val Griffe-d’Acier.', 'Près de l’extrémité sud de notre forêt, vous trouverez le tertre du Dormeur.$b$bC’est là que l’archidruidesse Koda Griffe-d’Acier et ses fidèles disciples accomplissent leur mission sacrée en observant le Rêve d’émeraude.$b$bRetrouvez la disciple Rylissa Chantelours et faites-lui part de la crise à laquelle nous devons faire face dans le bosquet de Cénarius.', '', '', '', '', '', 'Parlez à Rylissa Chantelours dans le Val Griffe-d’Acier.', '26365');
