INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('4012200', 'frFR', 'Cénarius, gardien du Bosquet', 'Parlez à Malfurion Hurlorage à Lorlathil, en Val’sharah.', 'Ah, Val’sharah.$b$bCela fait une éternité que je n’ai pas mis les pieds dans ces bois sacrés. Ils me manquaient…$b$bLe travail que nous avons à accomplir ici est fort simple, $c. Nous devons faire appel à mon mentor, Cénarius, afin d’apprendre où se trouvent les Larmes d’Élune. Grâce à elles, nous aurons de meilleures chances de sceller la tombe de Sargeras et de tenir la Légion en échec.$b$bDe nombreuses tâches difficiles vous attendent. Considérez votre passage en ce lieu comme un bref répit.$b$bVenez, ce ne sera pas long.', '', '', '', '', '', '', '26365');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('4012200', 'deDE', 'Cenarius, Hüter des Hains', 'Sprecht mit Malfurion Sturmgrimm in Lorlathil in Val\'sharah.', 'Ahh, Val\'sharah.$b$bEs ist schon lange her, dass ich einen Fuß in diese heiligen Wälder gesetzt habe. Viel zu lange...$b$bUnsere Aufgabe hier ist einfach, $C. Wir müssen meinen Mentor, Cenarius, rufen und herausfinden, wo er die Tränen von Elune verwahrt. Sie werden Euch unserem Ziel näherbringen, Sargeras\' Grab zu versiegeln und die Legion aufzuhalten.$b$bAuf Euch warten schwere Prüfungen. Euer Aufenthalt hier ist lediglich eine Atempause von kurzer Dauer.$b$bKommt, das wird nicht viel Zeit in Anspruch nehmen.', '', '', '', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('4012200', 'itIT', 'Cenarius, Custode della Selva', 'Parla con Malfurion Grantempesta a Lorlathil nella regione di Val\'sharah.', 'Ah, Val\'sharah.$b$bSono passati anni da quando ho messo piede in questi sacri boschi. Troppi anni...$b$bIl nostro lavoro qui è molto semplice, $C. Dobbiamo convocare il mio mentore, Cenarius, e capire dove ha nascosto le Lacrime di Elune. Dopo averle recuperate, avremo una possibilità di sigillare la Tomba di Sargeras e fermare la Legione una volta per tutte.$b$bMolti compiti pericolosi ti attendono. Considera la tua visita qui una breve pausa.$b$bVieni, non ci metteremo molto.', '', '', '', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('4012200', 'ptBR', 'Cenarius, o Guardião do Bosque', 'Fale com Malfurion Tempesfúria em Lorlathil, que fica em Val\'sharah.', 'Ah, Val\'sharah.$b$bHá tempos não ponho os pés nesta mata sagrada. Há eras...$b$bNosso trabalho é simples, $c. Temos que perguntar ao meu mentor, Cenarius, onde ele guarda as Lágrimas de Eluna. Com elas, você estará mais perto de selar a Tumba de Sargeras e derrotar a Legião.$b$bHá muitas provações à sua frente. Considere essa visita como um breve respiro.$b$bVenha, não vai tomar muito tempo.', '', '', '', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('4012200', 'koKR', '숲의 수호자 세나리우스', '발샤라의 로르라실에 있는 말퓨리온 스톰레이지와 대화해야 합니다.', '아, 발샤라.$b$b내가 이 신성한 숲에 발을 들이는 것도 정말 오랜만이군. 너무 오랜만이야...$b$b여기서 해야 할 일은 간단하다, $c|1이여;여;. 세나리우스 스승님을 만나 엘룬의 눈물이 있는 곳을 알아내는 것이지. 그 유물이 있으면 살게라스의 무덤을 봉인하고 군단을 저지하는 목표에 한 걸음 더 다가갈 수 있다.$b$b그대는 앞으로 험난한 길을 헤쳐나가야 하니, 이곳에서 한숨 돌린다고 생각하면 되겠군.$b$b가자. 그리 오래 걸리지는 않을 것이다.', '', '', '', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('4012200', 'zhCN', '森林守护者，塞纳留斯', '在瓦尔莎拉的洛拉希尔与玛法里奥·怒风谈一谈。', '啊，瓦尔莎拉。$b$b我上次踏入这片神圣的林地，已经是上个时代的事了。时光荏苒……$b$b这次的工作很简单，$c。我们需要拜访我的导师塞纳留斯，并询问他保管艾露恩之泪的位置。有了这件圣物，离封印萨格拉斯之墓并瓦解燃烧军团，就更近了一步。$b$b你以后会面对许多艰难险阻，不如在这里稍稍喘口气。$b$b来吧，这不需要花太久时间。', '', '', '', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('4012200', 'zhTW', '森林守護者，塞納留斯', '在維爾薩拉的洛拉希爾與瑪法里恩‧怒風交談。', '啊，維爾薩拉。$b$b我上次踏進這片神聖的林地，已經是很久以前的事了。太久了…$b$b我們在這裡的工作非常簡單，$c。我們必須尋訪我的導師塞納留斯，並請教他保管伊露恩之淚的位置。有了伊露恩之淚，離封印薩格拉斯之墓和瓦解燃燒軍團，就又更進一步。$b$b在你面前的，還有無數危險至極的任務。你可以在這裡稍稍喘息。$b$b來吧，這不需要花太多時間。', '', '', '', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('4012200', 'ruRU', 'Кенарий, Хранитель Рощи', 'Поговорите с Малфурионом Яростью Бури в Лорлатиле, что в Валь\'шаре.', 'Валь\'шара...$b$bКажется, целый век прошел с тех пор, как моя нога последний раз ступала по этому лесу. Это было так давно... $b$bНаша задача здесь проста, |3-6($c). Мы должны призвать Кенария, моего наставника, и выяснить, где он прячет Слезы Элуны. С ними мы еще на шаг приблизимся к нашей конечной цели – запечатать гробницу Саргераса и сорвать планы Легиона.$b$bТебе предстоит выполнить немало сложных и опасных поручений. Считай свое пребывание здесь небольшой передышкой.$b$bПойдем. Это не займет много времени.', '', '', '', '', '', '', '25549');
