INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('4413700', 'deDE', 'Auf ins Getümmel', 'Sprecht mit dem kommandierenden Dämonenjäger am Verteidigungsposten der Illidari.', 'Die Brennende Legion hat hier einen Sieg errungen, aber die Illidari auch. Anscheinend können die Dämonenjäger dem Ansturm standhalten... wenn auch nur mit Müh und Not.\n\nKommt, setzen wir Eure Waffe sinnvoll ein. Ich werde unsere nächsten Schritte planen, während Ihr die Legion niederkämpft.', '', '', '', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('4413700', 'itIT', 'Nel vivo della lotta', 'Parla con il capo dei Cacciatori di Demoni al Baluardo degli Illidari.', 'La Legione Infuocata ci ha sconfitti qui, ma lo stesso si può dire degli Illidari. Sembra che i Cacciatori di Demoni siano in grado di reggere il confronto, ma a malapena.\n\nVieni, vediamo di usare quella tua arma. Penserò alla prossima mossa mentre tu distruggerai la Legione.', '', '', '', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('4413700', 'ptBR', 'Ao combate', 'Fale com o líder dos caçadores de demônios na Fortaleza Illidari.', 'A Legião Ardente nos derrotou aqui, mas os Illidari também. Parece que os caçadores de demônios ainda estão de pé, mas por pouco.\n\nVenha, vamos fazer bom uso dessa sua arma. Eu planejo nosso próximo passo enquanto você esmaga a Legião.', '', '', '', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('4413700', 'koKR', '참전', '일리다리 격전지에 있는 악마사냥꾼 선임자와 대화해야 합니다.', '불타는 군단과 일리다리가 우리보다 먼저 이곳에 도착했소. 악마사냥꾼들이 악마에게 맞서곤 있지만, 아무래도 매우 위태로운 상황인 것 같소.\n\n갑시다. 당신의 무기를 써먹어야 할 때가 왔소. 당신이 군단을 물리치는 동안 난 다음 행보를 생각해 보겠소.', '', '', '', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('4413700', 'zhCN', '投入战斗', '在伊利达雷哨所与恶魔猎手的首领谈一谈。', '燃烧军团在这里打败了我们，但是伊利达雷还没有倒下。看来恶魔猎手们正在独力坚守阵线，但是只能勉强支撑。\n\n来吧，让你的武器发挥作用。在你痛击燃烧军团的时候，我会想出下一步行动。', '', '', '', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('4413700', 'zhTW', '身先士卒', '在伊利達瑞陣地和帶頭的惡魔獵人交談。', '燃燒軍團在這裡打敗了我們，包括伊利達瑞在內。看來惡魔獵人正在獨力堅守陣線，但只能勉強維持。\n\n來，讓你的武器發揮作用吧。在你痛擊燃燒軍團的時候，我會想出下一步該怎麼走。', '', '', '', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('4413700', 'ruRU', 'В гущу боя', 'Поговорите с командиром охотников на демонов на Аванпосте иллидари.', 'Пылающий Легион опередил нас, но и иллидари прибыли раньше. Пока что охотники на демонов вроде бы держатся, но их положение очень шатко.\n\nДумаю, самое время обратить мощь твоего оружия против нашего общего врага. А пока ты будешь крушить прислужников Легиона, я прикину, каким будет наш следующий шаг.', '', '', '', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('4413700', 'frFR', 'Dans la mêlée', 'Allez voir le chef des chasseurs de démons au Séjour des Illidari.', 'La Légion ardente est arrivée avant nous, mais les Illidari aussi. On dirait que les chasseurs de démons tiennent bon, mais pour combien de temps encore ?\n\nVenez, il est temps de vous servir de votre arme. Pendant que vous écrasez la Légion, je vais planifier notre prochaine intervention.', '', '', '', '', '', '', '26365');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('4413700', 'esES', 'A la refriega', 'Habla con el cazador de demonios líder en la Avanzada Illidari.', 'La Legión Ardiente ha llegado antes que nosotros, pero los Illidari también. Parece que los cazadores de demonios se defienden, aunque a duras penas.\n\nVamos, echemos mano de esa arma tuya. Yo pensaré qué hacemos mientras tú aplastas a la Legión.', '', '', '', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('4413700', 'esMX', 'A la refriega', 'Habla con el cazador de demonios líder en el Puesto Illidari.', 'La Legión Ardiente llegó aquí antes que nosotros, al igual que los Illidari. Parece que los cazadores de demonios siguen en pie, pero a duras penas.\n\nVamos, usemos esa arma tuya. Pensaré en nuestra próxima jugada mientras destruyes a la Legión.', '', '', '', '', '', '', '25549');
