INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938300', 'deDE', 'Ehrlos', 'Tötet Xandris den Ehrlosen.', 'Xandris der Ehrlose gehörte zu den Ersten, die ihr Volk verraten und Xavius\' Fluch angenommen haben.$B$BWährend des Krieges war er der Anführer der Truppen, die viele meiner besten Freunde umgebracht haben.$b$bAls wir uns das letzte Mal auf dem Schlachtfeld begegneten, ist er geflohen. Helft mir, endlich Rache an dieser widerwärtigen Kreatur zu nehmen.', '', '', 'Xandris der Ehrlose', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938300', 'itIT', 'Il Disonorato', 'Uccidi Xandris il Disonorato.', 'Xandris il Disonorato è stato uno dei primi a tradire i suoi fratelli e ad accettare la maledizione di Xavius.$B$BDurante la Grande Guerra, è stato lui a ordinare il massacro di molti dei miei amici.$b$bL\'ultima volta che l\'ho incontrato sul campo di battaglia, è scappato. Aiutami a dispensare la giustizia su questa creatura maligna.', '', '', 'Xandris il Disonorato', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938300', 'ptBR', 'Desonrado', 'Mate Xandris, o Desonrado.', 'Xandris, o Desonrado, foi um dos primeiros a trair seus irmãos e aceitar a maldição de Xavius.$B$BDurante a grande guerra, ele liderou o massacre de muitos de meus queridos amigos.$b$bNa última vez que o confrontei no campo de batalha, ele fugiu. Me ajude a fazer esse ser desprezível finalmente pagar pelo que fez.', '', '', 'Xandris, o Desonrado', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938300', 'koKR', '명예롭지 못한 자', '불명예의 잔드리스를 처치해야 합니다.', '불명예의 잔드리스는 우리 중 가장 먼저 형제를 배신하고 자비우스의 저주를 받아들였다.$B$B대전쟁 중에는 형제들을 학살하는 일에 앞장섰던 자이기도 하다.$b$b마지막으로 전장에서 마주쳤을 때는, 놈이 비겁하게 달아나 버렸지. 나와 함께 이 비열한 자에게 정의의 심판을 내리자.', '', '', '불명예의 잔드리스', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938300', 'zhCN', '耻辱者的制裁', '消灭可耻的萨德里斯。', '可耻的萨德里斯是最先背叛同胞，接受萨维斯诅咒的人之一。$B$B大战时，就是他带头杀害了我的许多亲朋好友。$b$b我最后一次在战场上和他对峙时，让他给逃走了。你要帮我制裁这个邪恶的东西。', '', '', '可耻的萨德里斯', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938300', 'zhTW', '夜精靈的恥辱', '殺死『恥辱者』山德里斯', '『恥辱者』山德里斯是首批背叛同伴，接受薩維斯詛咒的夜精靈。$B$B在先祖之戰中，他帶頭殺死我許多摯友。$b$b我們最後一次在戰場見面時，他竟然逃走了。請你幫我主持正義，解決這個邪惡的東西。', '', '', '『恥辱者』山德里斯', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938300', 'ruRU', 'Бесчестный', 'Убейте Ксандриса Бесчестного.', 'Ксандрис Бесчестный был среди первых сатиров, которые предали своих братьев и переметнулись к Ксавию.$B$BВ великую войну именно Ксандрис уничтожил множество моих товарищей.$b$bВ последний раз, когда мы с ним встречались на поле боя, он сбежал. Помоги мне покончить с этим гнусным созданием.', '', '', 'Ксандрис Бесчестный', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938300', 'esES', 'Deshonrado', 'Mata a Xandris el Deshonrado.', 'Xandris el Deshonrado fue de los primeros en traicionar a sus hermanos y aceptar la maldición de Xavius.$b$bDurante la gran guerra, fue él quien mandó asesinar a muchos de mis amigos más queridos.$b$bLa última vez que lo vi en el campo de batalla, huyó. Ayúdame a hacer justicia por fin con ese ser repugnante.', '', '', 'Xandris el Deshonrado', '', '', '', '25549');
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES ('3938300', 'esMX', 'Deshonroso', 'Mata a Xandris el Deshonroso.', 'Xandris el Deshonroso fue uno de los primeros en traicionar a los suyos y aceptar la maldición de Xavius.$B$BDurante la gran guerra, fue él quien lideró la masacre de muchos de mis queridos amigos.$b$bLa última vez que lo vi en el campo de batalla, huyó. Ayúdame a impartirle justicia a este ser repugnante.', '', '', 'Xandris el Deshonroso', '', '', '', '25549');
